C--   Write x-Q2 evolution grid
C--   -------------------------

      WRITE(LUN,'(/'' QCDNUM x-Q2 evolution grid'')')
      WRITE(LUN,'( '' --------------------------'')')

      CALL GRGIVE(N_X,X_MI,X_MA,N_Q,Q_MI,Q_MA)
     
      WRITE(LUN,'(/''   nx      xmin      xmax'',
