      IF(IQL.GE.IQH) THEN
        IERR = 2
        GOTO 500
      ENDIF
      IF(IQL.LT.1.OR.IQL.GE.NQ2) THEN
        IERR = 3
        GOTO 500
      ENDIF
      IF(IQH.LE.1.OR.IQH.GT.NQ2) THEN
        IERR = 3
