      NFMA = MAX(NFMA,NFMAP(IQ))
  15  CONTINUE
 
      DO 16 NF = NFMI,NFMA
        ID = IDCHEK(NAME,NF,'EVOLNP',1)
  16  CONTINUE
 
      IRUN = 0
      IF(.NOT.LPLUS) THEN
        DO 19 NF  = 3,5
        DO 18 IX0 = 1,NXX
