      LE_DONE(IX) = LEVDONE(IX,ID)
      DO IQ = 1,NQ2
        FNSQCD(IX,IQ) = PDFQCD(IX,IQ,ID)
      ENDDO   
      ENDDO   
 
      IF(IQ0.NE.IQ0_LAST(ID) .OR.
     +   IQL.NE.IQL_LAST(ID) .OR.
     +   IQH.NE.IQH_LAST(ID)     ) IRUN = 1
 
