      IQL_LAST(ID) = IQL
      IQH_LAST(ID) = IQH
 
      DO IX = 1,NXX
      LEVDONE(IX,ID) = LE_DONE(IX)
      DO IQ = 1,NQ2
        PDFQCD(IX,IQ,ID) = FNSQCD(IX,IQ)
