     +ALPHA0, Q0ALFA, ASLAST, QALAST,
     +ALFASQ(MQ2), ALFAPQ(MQ2), ALFA2Q(MQ2),
     +DELUP(MQ2), DELDN(MQ2), PDFQCD(MXX,MQ2,0:10),
