      COMMON/QCGRID/
     +SCAX0,SCAQ0,XMICUT,QMICUT,QMACUT,RS2CUT,QMINAS,
     +XXTAB(MXX),Q2TAB(MQ2),XHTAB(MXX),THRS34,THRS45,
     +NXX,NQ2,NGRVER,IHTAB(MXX),NFMAP(MQ2),IQF2C(MQ2),
     +IQF2B(MQ2),IQFLC(MQ2),IQFLB(MQ2),IFAILC(MXX,MQ2)
 
 
      REAL
     +WGTFF1,WGTFG1,
     +WGTGF1,WGTGG1,
     +WGTPP2,WGTPM2,WGTNS2,
     +WGTFF2,WGTFG2,
     +WGTGF2,WGTGG2,
