  // Cov(<4>,<sin(phi1)>):
  Double_t product9 = fIntFlowProductOfCorrectionTermsForNUAPro->GetBinContent(9); // <<4><sin(phi1)>>
  Double_t term1st9 = fIntFlowCorrelationsPro->GetBinContent(2); // <<4>>
