Int_t AliFlowAnalysisCRC::GetCRCBin(Int_t cf, Int_t yf, Int_t c2f, Int_t y2f)
{
  Int_t CRCBin=0;
  Int_t bin=1;
  for(Int_t c=0;c<2;c++) {
    for(Int_t y=0;y<2;y++) {
      for(Int_t c2=0;c2<2;c2++) {
        for(Int_t y2=0;y2<2;y2++) {
          if (c==cf && y==yf && c2==c2f && y2==y2f) CRCBin = bin;
          else bin++;
        }
      }
    }
  }
  return CRCBin;
} // end of AliFlowAnalysisCRC::GetCRCBin();

