  # Slide 2: Clusters plots --------------------------
  slide = qa.slides.add_slide(blank_slide_layout)
  imgWidth = 3.3
  topEdge = 1.5
  
  # Title
  txBox = slide.shapes.add_textbox(titleleft, titletop, titlewidth, titleheight)
  tf = txBox.text_frame
  tf.text = "Run " + str(run)
