 Double_t AliLnGaussianExpTail::evaluate() const 
 { 
// from pi/K/p note

	if(x <= mu + tau)
	{
		return exp(-0.5*(x-mu)*(x-mu)/sigma/sigma);
	}
	
	return exp(-0.5*tau*tau/sigma/sigma)*exp(-tau*(x-mu-tau)/sigma/sigma);
 } 



